/*
 * Minimal Bootstrap JS replacement
 *
 * This file provides a very small subset of the functionality of the
 * original Bootstrap JavaScript library.  Its sole purpose in this
 * project is to prevent 404 errors when the templates load
 * ``/static/js/bootstrap.min.js`` and to implement simple toggle
 * behaviour for the collapsed navigation bar on small screens.
 *
 * The implementation below listens for click events on the element with
 * class ``navbar-toggle`` and toggles the display of the navbar
 * collapse container with id ``bs-example-navbar-collapse-1``.  It does
 * not implement any other Bootstrap components such as modals,
 * dropdowns, tooltips, etc.
 */

document.addEventListener('DOMContentLoaded', function () {
  var toggler = document.querySelector('.navbar-toggle');
  var collapse = document.getElementById('bs-example-navbar-collapse-1');
  if (toggler && collapse) {
    toggler.addEventListener('click', function (e) {
      e.preventDefault();
      // Toggle display property between none and block
      if (collapse.style.display === 'block') {
        collapse.style.display = 'none';
      } else {
        collapse.style.display = 'block';
      }
    });
  }
});